import React from "react";

import Square from "./square";

function Board(props) {
  const renderSquare = (i) => (
    <Square value={props.squares[i]} onClick={props.onClick(i)} />
  );

  return (
    <div>
      <div className="board-row">
        {renderSquare(1)}
        {renderSquare(2)}
        {renderSquare(3)}
      </div>
      <div className="board-row">
        {renderSquare(4)}
        {renderSquare(5)}
        {renderSquare(6)}
      </div>
      <div className="board-row">
        {renderSquare(7)}
        {renderSquare(8)}
        {renderSquare(9)}
      </div>
    </div>
  );
}
export default Board;
